/*Table structure for table `p_users` */

DROP TABLE IF EXISTS `p_users`;

CREATE TABLE `p_users` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `openid` varchar(60) COLLATE utf8_unicode_ci NOT NULL,
  `wx_name` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '微信昵称',
  `wx_avatar` varchar(300) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '微信头像',
  `phone` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
  `name` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '家长姓名',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态，0禁用，1正常',
  `type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '0顾客，1理发师',
  `work_no` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '理发师工号',
  `vip_id` int(10) DEFAULT NULL COMMENT '会员卡id',
  `vip` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '会员名称',
  `vip_type` tinyint(4) DEFAULT '0' COMMENT '会员卡类型',
  `vip_exp_at` date DEFAULT NULL COMMENT 'VIP到期时间',
  `member_no` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '会员卡号',
  `remember_token` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `balance` decimal(10,2) DEFAULT '0.00',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `is_old` tinyint(3) DEFAULT '0' COMMENT '是否老用户:0:新，1：旧用户',
  PRIMARY KEY (`id`),
  KEY `users_openid_status_type_vip_phone_index` (`openid`,`status`,`type`,`vip`,`phone`)
) ENGINE=MyISAM AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

